CREATE DEFINER=`root`@`localhost` FUNCTION `edad_actual`(birthdate DATE) RETURNS int(11)
    DETERMINISTIC
RETURN YEAR(CURDATE()) - YEAR(birthdate) - (DATE_FORMAT(CURDATE(), '%m%d') < DATE_FORMAT(birthdate, '%m%d'))$$

